# Retrieves the details of a product by its ID, including the name, description, and hero image.
query GetProduct($productId: ID!) {
  product(id: $productId) {
    name
    image
    price {
      currency
      amount
      __typename
    }
    __typename
  }
}

# A list of all our snowboards in our catalog
query ListAllProducts {
  products {
    id
    name
    image
    __typename
  }
}

# Search snowboards in our product catalog
query SearchProducts($for: String!) {
  search(for: $for) {
    name
    image
    price {
      currency
      amount
      __typename
    }
    __typename
  }
}
