# Retrieves the details of a product by its ID, including the name, description, and hero image.
query GetProduct($productId: ID!) {
  product(id: $productId) {
    name
    description
    images {
      hero
      __typename
    }
    price {
      unitAmount
      __typename
    }
    __typename
  }
}

# Retrieves a list of products including their name and hero images.
query ListAllProducts {
  products {
    id
    name
    images {
      hero
      __typename
    }
    __typename
  }
}
