# Fetches the line items for a specific abandoned checkout, including custom attributes and discounted total prices.
query CheckoutLineItems($checkoutId: ID!) {
  abandonedCheckout(id: $checkoutId) {
    lineItems(first: 10) {
      edges {
        node {
          id
          customAttributes {
            key
            value
            __typename
          }
          discountedTotalPriceSet
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}

# Fetches the orders associated with a specific customer, including order IDs, total prices, and statuses.
query CustomerOrders($customerId: ID!) {
  customer(id: $customerId) {
    orders(first: 10) {
      edges {
        node {
          id
          totalPrice
          status
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}

# Retrieves the details of a product by its ID, including the title, description, and price range.
query GetProduct($productId: ID!) {
  product(id: $productId) {
    id
    title
    description
    priceRangeV2 {
      maxVariantPrice {
        amount
        currencyCode
        __typename
      }
      minVariantPrice {
        amount
        currencyCode
        __typename
      }
      __typename
    }
    __typename
  }
}

# Retrieves a list of customers including their IDs, first names, last names, and email addresses.
query ListCustomers($first: Int) {
  customers(first: $first) {
    edges {
      node {
        id
        firstName
        lastName
        email
        __typename
      }
      __typename
    }
    __typename
  }
}

# Retrieves a list of products including their IDs, titles, descriptions, and prices.
query PaginateThroughProducts($numberOfProductVariantsToTake: Int = 10) {
  products(first: $numberOfProductVariantsToTake) {
    edges {
      product: node {
        id
        title
        variants(first: $numberOfProductVariantsToTake) {
          nodes {
            title
            price
            inventoryQuantity
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}

# Retrieves a list of abandoned checkouts including their IDs, recovery URLs, and total prices.
query RetrieveAbandonedCheckouts {
  abandonedCheckouts(first: 10) {
    edges {
      node {
        id
        abandonedCheckoutUrl
        totalPriceSet
        __typename
      }
      __typename
    }
    __typename
  }
}
